import React from 'react';

//Navegacion
// import { Link, useNavigate, useLocation } from 'react-router-dom';

import BtnPrimary from '../components/btn_primary';

import "../stylesheet/views/registro.css"
import login from '../img/login.jpeg'


function Registro(){
    //Navegacion
    // const navigate = useNavigate();
    // const location = useLocation();
    return(

        <div className='main-container-registro'>
            <div className='header'><h1>Registrarse</h1></div>
            <div className='form'>
                <form className='form2' action="POST">
                    <div>
                        <p>Nombre :</p><input className='input-generico' type="text" name='Nombre' required id='Nombre'/>
                    </div>
                    <div>
                        <p> Apellido :</p><input className='input-generico' type="text" name='Apellido' required/>
                    </div>
                    <div>
                        <p> Correo electrónico :</p><input className='input-generico' placeholder = 'mi_correo@mail.com' type="email" name='Mail' required/>
                    </div>
                    <div>
                        <p> Contraseña :</p><input className='input-generico' placeholder = '****' type="password" name='pwd' required/>    
                    </div>
                    <div>
                        <p> Confirmar contraseña :</p><input className='input-generico' placeholder = '****' type="password" name='pwd2' required/>
                    </div>
                    <div className='divButton'>
                        <BtnPrimary ruta={'/'} name={'Registrarse'}/>
                    </div>

                </form>
                <div className='img-registro'>

                    <div className='img_registro2'>
                        <img src={login} alt="" />
                    </div>

                    </div>
                </div>
        </div>

    )


}
export default Registro;
