//Estilos
import { useState } from 'react'
import '../stylesheet/componentes/slide_images.css'

function Carousel ({url_imagen_1, url_imagen_2, url_imagen_3, url_imagen_4, url_imagen_5, url_imagen_6}) {

    const [carousel, setCarousel] = useState([])
    const [aux, setAux] = useState(0)
    const [cont_img] = useState(0) 
    // setCarousel = [url_imagen_1,url_imagen_2,url_imagen_3,url_imagen_4,url_imagen_5,url_imagen_6]

    const adelante = () => {

        if (aux < carousel.length-2){
            setAux(aux+1)
        }
        else {
            setAux(0)
        }
        return aux

    }
    const atras = () => {
        if (aux > 0){
            setAux(aux-1)
        }
        else{
            setAux(carousel.length-2)
        }
        return aux
    }


    return(
        <div className="slide_images">
            <button className="btn-back" onClick={adelante}>A</button>

            <div className="div_img_slide_1">
                <img className="slide_url_img" src={carousel[aux+2]} alt="imagen 1" /> 
            </div>
            <div className="div_img_slide_2">
                <img className="slide_url_img" src={carousel[aux+1]} alt="imagen 2" /> 
            </div>
            <div className="div_img_slide_3">
                <img className="slide_url_img" src={carousel[aux]} alt="imagen 3" /> 
            </div>
            <div className="div_img_slide_2">
                <img className="slide_url_img" src={carousel[carousel.length-aux-1]} alt="imagen 4" /> 
            </div>
            <div className="div_img_slide_1">
                <img className="slide_url_img" src={carousel[carousel.length-aux-2]} alt="imagen 5" /> 
            </div>

            <button className="btn-back" onClick={atras}>B</button>
        </div>
    )
}

export default Carousel
