import React from "react";
import Axios from "axios";

// Componentes
import BtnPrimary from "../components/btn_primary";

// Estilos
import "../stylesheet/views/login.css"

//Imagen
import login from '../img/login.jpeg'

//Back
const express = require("express");
const db = require("../../../api2/config/db");
const cors = require("cors");

const app = express();
const PORT = 8891;

app.use(cors());
app.use(express.json());


function Login() {

    // const getAll = () => {
    //     Axios.get(`http://localhost:8888/api/get`).then((response)=>{
    //         alert("you deleted a post")
    //     })
    // }

    // Route to get all posts
    app.get("/api/get", (req, res) => {
        db.query("SELECT * FROM User", (err, result) => {
        if (err) {
            console.log(err);
        }
        console.log(result);
        res.send(result);
        });
    });

    return (
        <div className="login">
            <div className="left-form">
                <h1>Iniciar Sesion</h1>

                <form  className="login-form" action="">
                    <div className="email-login">
                        <p>Correo</p>
                        <input className="input-generico2" placeholder="correo@ejemplo.com" type="email" name="email" id="email" />
                    </div>
                    <div className="pass-login">
                        <p>Contraseña</p>
                        <input className="input-generico2" placeholder="contraseña" type="password" name="pass" id="pass" />
                    </div>
                    <BtnPrimary name={"Iniciar Sesion"}/>
                    <h3>Registrarse</h3>
                    <BtnPrimary ruta={"Registro"} name={'Registrarse'} />
                </form>

                {/* <button onClick={() => navigate("/Registro")}>Registrarse</button> */}
            </div>
            <div className="rigth-form">
                <img className="img-login" src={login} alt="" />
            </div>
        </div>
    )
}

export default Login;
