import React from "react";

// Componente
import FormEditar from "./form_editar";

// Estilos
import '../stylesheet/componentes/AccionesAdmin.css'
import { useState } from "react";

//BACK
import Axios from "axios";
//Back
import axios from "axios";

function AccionesAdmin({ accion, titulo, id, tabla }) {

    console.log('ESTE ES EL ID EN ACCIONES ADMIN,',id)
    const [flagEdit, setFlagEdit] = useState('False')
    const [id_aux, setId] = useState('')
    const [tablaAux, setTablaAux] = useState('')
    const editando = () => {
        console.log("Este es mi id:", id)
        setTablaAux(tabla)
        setId(id)
        setFlagEdit('True')
    }
    const cancelar = () => {
        setFlagEdit('False')
    }

    const Eliminar = () => {
        setTablaAux(tabla)
        setId(id)
        console.log("Hice click",tablaAux)
        if (tablaAux == 'blog'){
            EliminarBlog()
        }
        if (tablaAux == 'card'){
            EliminarCard()
        }

    }

    const EliminarBlog = async () => {
        try {
            const respData = await Axios.post(`http://localhost:8891/id/deleteFromBlog`, { id: id_aux });
            if (respData.data.ok) {
                console.log('Se ha eleminado el id ', id_aux)
            }

        } catch (error) {
            console.log(error)

        }
    }

    const EliminarCard = async () => {
        try {
            const respData = await Axios.post(`http://localhost:8891/id/deleteFromCard`, { id: id_aux });
            if (respData.data.ok) {
                console.log('Se ha eleminado el id ', id_aux)
            }

        } catch (error) {
            console.log(error)

        }
    }

    return (
        <div className="div-accion-main">
            {/* <h2>{accion}</h2> */}
            <p className="titulo-acciones">{titulo}</p>
            <div className="div-btn-acciones">
                <button className="btn-acciones-editar" onClick={editando}>Editar</button>
                {flagEdit == 'True' && <FormEditar id={id_aux} cancelar={cancelar} func={editando} tabla={tablaAux}/>}

                <button onClick={Eliminar} className="btn-acciones-eliminar">Eliminar</button>
            </div>
            {/* <button onClick={editar}>Cancelar</button> */}
        </div>

    )
}

export default AccionesAdmin
