import React from "react";
import { useState } from "react";
//BACK
import Axios from "axios";
//Back
import axios from "axios";
import { useEffect } from "react";

//Componentes
import BtnPrimary from "./btn_primary";

//estilos
import '../stylesheet/componentes/form_editar.css'

function FormEditar({id, editar, func}) {

    const [titulo, setTitulo] = useState('')
    const [informacion, setInformacion] = useState('')
    const [imageURL, setImageURL] = useState('')
    const [fuente, setFuente] = useState('')
    const [fecha, setFecha] = useState('')

    //Use Effect para cargar informacion del back
    useEffect(() => {
        document.title = 'Tu salud informa'
        editarBack()


    }, [id])

    /*Funcion para rellenar el formulario de edicion*/
    const editarBack = async () => {

        try {
            const respData = await Axios.get(`http://localhost:8891/id/getCard`, {id:id});
            if (respData.data.ok) {
                setTitulo(respData.data.titulo)
                setInformacion(respData.data.informacion)
                setImageURL(respData.data.imgURL)
                setFecha(respData.data.fecha)
                setFuente(respData.data.fuente)

            }

        } catch (error) {
            console.log(error)

        }

    }

        /*Funcion para enviar el formulario de edicion a la base de datos*/
        const updateBlog = async () => {

            try {
                const respData = await Axios.post(`http://localhost:8891/updateBlog`, {id:id,titulo:titulo,informacion:informacion,imageURL:imageURL,fecha:fecha,fuente:fuente});
                if (respData.data.ok){
                    alert('Se han actualizado los datos de ',titulo)
                }
            } catch (error) {
                console.log(error)

            }

        }



    return (
        <div className="editar-form" action="">
            <h1>Iniciar Sesion</h1>
            <div className="titulo-form-editar">
                <p>Titulo:</p>
                <input onChange={(e) => setTitulo(e.target.value)} className="input-generico2" placeholder="Titulo" type="text" name="email" id="email" />{titulo}
            </div>
            <div className="inforacion-form-editar">
                <p>Información : </p>
                <input onChange={(e) => setInformacion(e.target.value)} className="input-generico2" placeholder="Descripción" type="text" name="pass" id="pass" />{informacion}
            </div>
            <div className="imagen-form-editar">
                <p>URL : </p>
                <input onChange={(e) => setImageURL(e.target.value)} className="input-generico2" placeholder="Imagen" type="text" name="pass" id="pass" />{imageURL}
            </div>
            <div className="fecha-form-editar">
                <p>Fecha : </p>
                <input onChange={(e) => setFecha(e.target.value)} className="input-generico2" placeholder="Fecha" type="text" name="pass" id="pass" />{fecha}
            </div>
            <div className="fuente-form-editar">
                <p>Fuente : </p>
                <input onChange={(e) => setFuente(e.target.value)} className="input-generico2" placeholder="Fuente" type="text" name="pass" id="pass" />{fuente}
            </div>
            <BtnPrimary funcion={updateBlog} name={"Guardar"} />
            {/* <h3>Registrarse</h3> */}
            <BtnPrimary funcion={func} name={'Cancelar'} />
        </div>
    )
}

export default FormEditar
