import React, { useEffect, useState } from "react";

//Componentes
import BtnPrimary from "../components/btn_primary";
import Card from "../components/Card";

//Navegacion
import { useNavigate } from "react-router-dom";

//BACK
import Axios from "axios";
import axios from "axios";

function Home() {

    //Funcion al cargar la pagina
    useEffect(() => {
        card()
    },[])

    const [infoCard, setInfoCard] = useState([])
    //Navegacion
    const navigate = useNavigate();

    //Card
    const card = () => {
        Axios.get(`http://localhost:8891/api/getCard`).then((response)=>{
            setInfoCard(response.data)
        })
    }

    //Funcion de navegacion
    const login = () =>{
        navigate("/")
    }

    return (
        <div className="home">
            <h2>Bienvenido</h2>
            {infoCard.map((e) => (
                <Card titulo = {e.titulo} 
                      informacion = {e.informacion} 
                      image = {e.image} 
                      fecha = {e.fecha} 
                      fuente = {e.fuente} 
                      key = {e.id}/>
            ))}
            {/* <BtnPrimary funcion={card} name={"Mostrar Card"}/> */}
            <BtnPrimary funcion={login} name={"Cerrar Sesion"}/>
        </div>
    )
}

export default Home
