import React, { useEffect, useState } from "react";
import Axios from "axios";

// Componentes
import BtnPrimary from "../components/btn_primary";

// Estilos
import "../stylesheet/views/login.css"

//Imagen
import login from '../img/login.jpeg'
import { useNavigate } from "react-router-dom";


function Login() {
    //NAvegacion
    const navigate = useNavigate();

    //Ingresar datos y guardarlos
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const ClickIniciar = () => {
        console.log("Este es el email: " + email)
        console.log("Esta es la contrase単a: " + password)
    }

    //Se ejecuta cada que la pagina se recarga
    useEffect(() => {
        users()
    },[])

    //Variables que guardan a los usuarios el primer valor es una variable (user), el segundo una funcion (setUser)
    const [user, setUser] = useState([])

    //Variable que contiene los datos extraidos del back
    const users = () => {
        Axios.get(`http://localhost:8891/api/get`).then((response)=>{
            setUser(response.data)
        })
    }

    //Funcion de navegacion
    const navegacion = () =>{
        navigate("/Registro")
    }


    return (
        <div className="login">
            <div className="left-form">

                <div  className="login-form" action="">
                    <h1>Iniciar Sesion</h1>
                    <div className="email-login">
                        <p>Correo</p>
                        <input onChange={(e) => setEmail(e.target.value)} className="input-generico2" placeholder="correo@ejemplo.com" type="email" name="email" id="email" />
                    </div>
                    <div className="pass-login">
                        <p>Contrase単a</p>
                        <input onChange={(e) => setPassword(e.target.value)} className="input-generico2" placeholder="contrase単a" type="password" name="pass" id="pass" />
                    </div>
                    <BtnPrimary name={"Iniciar Sesion"}/>
                    <h3>Registrarse</h3>
                    <BtnPrimary funcion={navegacion} name={'Registrarse'} />

                </div>

            </div>
            <div className="rigth-form">
                <img className="img-login" src={login} alt="" />
            </div>
        </div>
    )
}

export default Login;

