        try {
            const respData = await Axios.post(`http://localhost:8891/id/deleteFromBlog`, { id: id_aux });
            if (respData.data.ok) {
                console.log('Se ha eleminado el id ', id_aux)
            }


        } catch (error) {
            console.log(error)

        }
