import React from "react";

// Componentes
import Btn_primary from "../components/btn_primary";

// Estilos
import "../stylesheet/views/login.css"

//Imagen
import login from '../img/login.jpeg'

//db
const express = require('express');
const db = require('../config/db')
const cors = require('cors')

const app = express();
const  PORT = 8888;
app.use(cors());
app.use(express.json())

// Route to get all posts
app.get("/api/get", (req,res)=>{
    db.query("SELECT * FROM User", (err,result)=>{
        if(err) {
        console.log(err)
        } 
    res.send(result)
    });   });

app.listen(PORT, ()=>{
    console.log(`Server is running on ${PORT}`)
})

function Login() {
    return (
        <div className="login">
            <div className="left-form">
                <h1>Iniciar Sesion</h1>
                <form  className="login-form" action="">
                    {/* <div htmlFor="name">
                        <p>Nombre: </p>
                        <input className="input-generico" placeholder="Nombre" type="text" name="name" id="name" />
                    </div>
                    <div htmlFor="surname">
                        <p>Apellidos</p>
                        <input className="input-generico" placeholder="Apellido" type="text" name="surname" id="surname" />
                    </div> */}
                    <div htmlFor="email">
                        <p>Correo</p>
                        <input className="input-generico" placeholder="correo@ejemplo.com" type="email" name="email" id="email" />
                    </div>
                    <div htmlFor="pass">
                        <p>Contraseña</p>
                        <input className="input-generico" placeholder="contraseña" type="password" name="pass" id="pass" />
                    </div>
                    <Btn_primary name={"Iniciar Sesion"}/>
                    <h3>Registrarse</h3>
                </form>
                <Btn_primary name={'Registrarse'} />
            </div>
            <div className="rigth-form">
                <img className="img-login" src={login} alt="" />
            </div>
        </div>
    )
}

export default Login;
