// Componente
import FormEditar from "./form_editar";

// Estilos
import '../stylesheet/componentes/AccionesAdmin.css'
import { useState } from "react";

function AccionesAdmin({ accion, titulo, info, id }) {

    const [flagEdit, setFlagEdit] = useState('False')
    const editando = () => {
        console.log("Este es mi id:", id)
        setFlagEdit('True')
    }
    return (
        <div className="div-accion-main">
            <h2>{accion}</h2>
            <p>{titulo}</p>
            <div>
                <button onClick={editando}>Editar</button>
                {flagEdit == 'True' && <FormEditar id={id} func={editando} />}

                <button>eliminar</button>
            </div>
            {/* <button onClick={editar}>Cancelar</button> */}
        </div>

    )
}

export default AccionesAdmin
