import React from "react";

// Componente
import FormEditar from "./form_editar";

// Estilos
import '../stylesheet/componentes/AccionesAdmin.css'
import { useState } from "react";

function AccionesAdmin({ accion, titulo, id, tabla }) {

    console.log('ESTE ES EL ID EN ACCIONES ADMIN,',id)
    const [flagEdit, setFlagEdit] = useState('False')
    const [id_aux, setId] = useState('')
    const [tablaAux, setTablaAux] = useState('')
    const editando = () => {
        console.log("Este es mi id:", id)
        setTablaAux(tabla)
        setId(id)
        setFlagEdit('True')
    }
    return (
        <div className="div-accion-main">
            <h2>{accion}</h2>
            <p>{titulo}</p>
            <div>
                <button onClick={editando}>Editar</button>
                {flagEdit == 'True' && <FormEditar id={id_aux} func={editando} tabla={tablaAux}/>}

                <button>eliminar</button>
            </div>
            {/* <button onClick={editar}>Cancelar</button> */}
        </div>

    )
}

export default AccionesAdmin
