import React, { useEffect, useState } from "react";

// Estilos
import "../stylesheet/componentes/Blog.css";

function Blog({ titulo, informacion, image, fecha, fuente }) {
    const [parrafo, setParrafo] = useState([]);

    const func_parrafo = () => {
        setParrafo(informacion.split("\n"));
    };

    const display_parrafo = (parrafo_aux) => {
        if (parrafo != []) {

            return (
                <p>
                    {parrafo_aux}
                    <br />
                </p>
            )
        }
        else {
            console.log('EN ELSE LEN PARRAFO', parrafo)
        }
    }

    useEffect(() => {
        func_parrafo();
    }, []);
    console.log('len: ', parrafo)

    return (
        // <div className="div-blog2">
        <div className="blog-contain">
            <h1>{titulo}</h1>
            <p>
                {parrafo.map((e) => {
                    display_parrafo(e)
                })}
            </p>
            <div className="img-card-blog">
                <img src={image} alt="Imagen" />
            </div>
            <h3>{fuente}</h3>
            <h4>{fecha}</h4>
        </div>
        // </div>
    );
}

export default Blog;
